from pydantic import BaseModelfrom pydantic_settings import (    BaseSettings,    SettingsConfigDict,)class BotConfig(BaseModel):    token: str    bot_name: strclass Settings(BaseSettings):    model_config = SettingsConfigDict(        env_file=("../env.example", "../.env"),        case_sensitive=False,        env_nested_delimiter="__",        env_prefix="APP_CONFIG__",        extra="ignore",    )    bot: BotConfigsettings = Settings()