from pydantic import BaseModelfrom pydantic_settings import (    BaseSettings,    SettingsConfigDict,)class BotConfig(BaseModel):    token: str    bot_name: strclass RedisStreamConfig(BaseModel):    stream: str = "add_stream"    consumer: str = "add_consumer"    group: str = "add_group"    maxlen: int = 1000class MessageLinksConfig(RedisStreamConfig):    stream: str = "message_links_stream"    consumer: str = "message_links_consumer"    group: str = "message_links_group"class RedisConfig(BaseModel):    url: str = "redis://localhost:6379"    add_stream: RedisStreamConfig = RedisStreamConfig()    message_links: MessageLinksConfig = MessageLinksConfig()class Settings(BaseSettings):    model_config = SettingsConfigDict(        env_file=("../env.example", "../.env"),        case_sensitive=False,        env_nested_delimiter="__",        env_prefix="APP_CONFIG__",        extra="ignore",    )    bot: BotConfig    redis: RedisConfig = RedisConfig()settings = Settings()